pragma solidity ^0.5.0;

contract Shop {
    address payable public owner;
    address[6] public buyers;

    // 商品价格（单位：wei），固定 6 个商品
    uint[6] public prices = [
        10000000000000000,  // 0.01 ETH
        5000000000000000,   // 0.005 ETH
        5000000000000000,
        5000000000000000,
        5000000000000000,
        5000000000000000
    ];

    event ProductPurchased(uint indexed productId, address indexed buyer, uint price);

    constructor() public {
        owner = msg.sender;
    }

    // 购买商品
    function buyProduct(uint productId) public payable returns (bool) {
        require(productId < 6, "商品编号无效");
        require(buyers[productId] == address(0), "商品已被购买");
        require(msg.value >= prices[productId], "付款金额不足");

        buyers[productId] = msg.sender;
        emit ProductPurchased(productId, msg.sender, msg.value);
        return true;
    }

    // 查看所有购买者地址
    function getBuyers() public view returns (address[6] memory) {
        return buyers;
    }

    // 合约所有者提取资金
    function withdraw() public {
        require(msg.sender == owner, "只有管理员可以提现");
        owner.transfer(address(this).balance);
    }

    // 查询商品价格
    function getPrice(uint productId) public view returns (uint) {
        require(productId < 6, "商品编号无效");
        return prices[productId];
    }
}
